<?php header('content-type: text/html; charset=utf-8');header('Access-Control-Allow-Origin: *');require_once('admin_mca/invedion_connect.php');$lng=addslashes(strip_tags($_GET['lng']));$cn=idbc();$cq=$cn->query("SELECT id FROM mca_lang WHERE short = '$lng' AND online ='1'");$show_lng=$cq->fetch_row();$question=$cn->query("SELECT id, title, description FROM mca_categories WHERE (id_lang = '$show_lng[0]' OR id_lang = 'all') AND online ='1' ORDER BY mca_order ASC");for($lp=0;$rz=$question->fetch_row();++$lp){$tab[$lp]=$rz;}if($question->num_rows>0){foreach($tab as $categories){$first_letter=substr($categories[1],0,1);?> <a href="content.html#idc=<?php echo $categories[0];?>&ct=<?php echo $categories[1];?>" class="menu"> <div class="menu_tl"><?php echo $first_letter;?></div> <div class="menu_tx"><?php echo $categories[1];?></div> <div class="menu_td"><?php echo $categories[2];?></div> </a> <?php }}else{?> <div class="ce" id="lx31">No results for this query</div> <?php }?>