<?php header('content-type: text/html; charset=utf-8');header('Access-Control-Allow-Origin: *');require_once('admin_mca/invedion_connect.php');?> <?php $idc=addslashes(strip_tags($_GET['idc']));if(!empty($idc)){?> <div style="margin:20px 0 40px 15px"> <?php $cn=idbc();$question=$cn->query("SELECT id, photo FROM mca_content WHERE id_category = '$idc' AND online ='1' ORDER BY mca_order ASC");for($lp=0;$rz=$question->fetch_row();++$lp){$tab[$lp]=$rz;}if($question->num_rows>0){foreach($tab as $content){?> <a href="#idb=<?php echo $content[0];?>" class="magazine"><img src="<?php echo 'http://'.$_SERVER['HTTP_HOST'].dirname($_SERVER['PHP_SELF']);?>/images/<?php echo $content[1];?>" width="300" height="420"></a> <?php }}else{?> <div class="ce" id="lx31">No results for this query</div> <?php }?> </div> <?php }?>