<?php header('content-type: application/json; charset=utf-8');header('Access-Control-Allow-Origin: *');require_once('admin_mca/invedion_connect.php');$cn=idbc();$login=$cn->real_escape_string($_GET['login']);$pass=$cn->real_escape_string($_GET['pass']);if($login&&$pass){$question=$cn->query("SELECT id, mail FROM mca_users WHERE mail = '$login' AND pass = sha1('$pass') AND online = '1'");if($question->num_rows>0){$login_show=$question->fetch_row();$json='{"mca":{"status":"ok","id":"'.$login_show[0].'","mail":"'.$login_show[1].'"}}';echo $_GET['callback'].'('.$json.')';}else{$json='{"mca":{"status":"no"}}';echo $_GET['callback'].'('.$json.')';}}else{$json='{"mca":{"status":"no"}}';echo $_GET['callback'].'('.$json.')';}?>