<?php header('content-type: text/html; charset=utf-8');header('Access-Control-Allow-Origin: *');require_once('admin_mca/invedion_connect.php');?> <?php $search=addslashes(strip_tags($_GET['search']));$cn=idbc();$question=$cn->query("SELECT id, title FROM mca_content_articles WHERE online ='1' AND (desc_full LIKE '%$search%' OR title LIKE '%$search%') ORDER BY id DESC");for($lp=0;$rz=$question->fetch_row();++$lp){$tab[$lp]=$rz;}if($question->num_rows>0){$no_ca=1;foreach($tab as $show_search){?> <a href="content.html#art=<?php echo $show_search[0];?>&search=<?php echo $search;?>" class="tc_list"><span class="tc_list_1_bg"><?php echo $no_ca;?>.</span> <span class="tc_list_2_bg"><?php echo $show_search[1];?></span></a> <?php $no_ca++;}}else{?><div class="ce_search" id="lx30">No results for this query</div> <?php } ?>